import React from 'react';
import { Link } from 'react-router-dom';

function Home() {
  return (
    <div className="hero-container">
      {/* 1. Background Video */}
      <video className="hero-video" autoPlay loop muted playsInline>
        {/* Free Pexels Tiger/Nature Video URL */}
        <source src="https://videos.pexels.com/video-files/5349767/5349767-hd_1920_1080_25fps.mp4" type="video/mp4" />
      </video>

      {/* 2. Hero Content */}
      <div className="hero-content">
        <div className="animate-up">
          <span style={{
            background:'rgba(16,185,129,0.2)', 
            color:'#10b981', 
            padding:'5px 15px', 
            borderRadius:'20px', 
            fontWeight:'600',
            textTransform: 'uppercase',
            fontSize: '0.9rem'
          }}>
            AI Powered Surveillance
          </span>
        </div>
        
        <h1 className="hero-title animate-up delay-1">
          Protecting Nature <br /> Through Intelligence
        </h1>
        
        <p className="hero-desc animate-up delay-2">
          An advanced Computer Vision system that detects wildlife in real-time, 
          identifies absent species, and ensures safety in restricted zones.
        </p>

        <div className="animate-up delay-3">
          <Link to="/test" className="btn-glow">
            Start Detection System
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Home;
