import React from 'react';

function Features() {
  const features = [
    {
      icon: "üîç",
      title: "Real-Time Detection",
      desc: "Uses advanced YOLO algorithms to identify animals instantly from live camera feeds with 98% accuracy."
    },
    {
      icon: "üìâ",
      title: "Absence Reporting",
      desc: "Automatically compares detected animals against a checklist to tell you exactly who is missing from the habitat."
    },
    {
      icon: "‚ö°",
      title: "Instant Alerts",
      desc: "Sends immediate notifications to rangers if an unknown predator enters a safe zone."
    },
    {
      icon: "üìä",
      title: "Data Logging",
      desc: "Keeps a historical log of all animal movements for research and pattern analysis."
    }
  ];

  return (
    <div className="page-container">
      <h2 className="section-title animate-up">System Capabilities</h2>
      
      <div className="features-grid">
        {features.map((item, index) => (
          <div key={index} className={`feature-card animate-up delay-${index + 1}`}>
            <div className="icon-box">{item.icon}</div>
            <h3 style={{fontSize: '1.5rem', marginBottom: '10px'}}>{item.title}</h3>
            <p style={{color: '#9ca3af', lineHeight: '1.6'}}>{item.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Features;
